mapper.update:
UPDATE /* com.qs.www.board.model.dao.FreeDAO#incrementFreeCount() */
       TBL_BOARD A
   SET A.BOARD_COUNT = (SELECT B.BOARD_COUNT
                           FROM TBL_BOARD B
                          WHERE B.BOARD_NO = #{ no }
                        ) + 1
 WHERE A.BOARD_NO = #{ no }

 
		UPDATE /* com.qs.www.board.model.dao.FreeDAO#updateFree()
		*/
		TBL_BOARD A
		SET A.BOARD_TITLE = #{ title }
		, A.BOARD_BODY = #{ body }
		WHERE A.BOARD_NO = #{ no }
		AND A.MEMBER_NO = #{ member }

mapper.delete:
DELETE
FROM TBL_BOARD A
WHERE A.BOARD_NO = #{ no }
