mapper.insert:
INSERT /* com.qs.www.board.model.dao.FreeDAO#insertFree() */
  INTO TBL_BOARD A
(
  A.BOARD_NO
, A.BOARD_TITLE
, A.MEMBER_NO
, A.BOARD_TYPE
, A.BOARD_COUNT
, A.BOARD_BODY
, A.BOARD_YN
, A.DELETE_YN
, A.CREATED_DATE
, A.MODIFIED_DATE
)
VALUES
(
  SEQ_BOARD_NO.NEXTVAL
, #{ title }
, #{ member }
, #{ type }
, #{ count }
, #{ body }
, #{ delete }
, SYSDATE
, SYSDATE
)

mapper.update:
UPDATE /* com.qs.www.board.model.dao.FreeDAO#incrementFreeCount() */
       TBL_BOARD A
   SET A.BOARD_COUNT = (SELECT B.BOARD_COUNT
                           FROM TBL_BOARD B
                          WHERE B.BOARD_NO = #{ no }
                        ) + 1
 WHERE A.BOARD_NO = #{ no }
