mapper.update:
UPDATE /* com.qs.www.board.model.dao.NoticeDAO#incrementNoticeCount() */
       TBL_BOARD A
   SET A.BOARD_COUNT = (SELECT B.BOARD_COUNT
                           FROM TBL_BOARD B
                          WHERE B.BOARD_NO = #{ no }
                        ) + 1
 WHERE A.BOARD_NO = #{ no }

mapper.select:
SELECT C.RNUM , C.BOARD_NO , C.BOARD_TITLE , C.MEMBER_NO , C.BOARD_TYPE , C.BOARD_COUNT , C.BOARD_BODY , C.DELETE_YN , C.CREATED_DATE , C.MODIFIED_DATE , C.NAME FROM (SELECT ROWNUM RNUM , B.BOARD_NO , B.BOARD_TITLE , B.MEMBER_NO , B.BOARD_TYPE , B.BOARD_COUNT , B.BOARD_BODY , B.DELETE_YN , B.CREATED_DATE , B.MODIFIED_DATE , B.NAME FROM (SELECT A.BOARD_NO , A.BOARD_TITLE , A.MEMBER_NO , A.BOARD_TYPE , A.BOARD_COUNT , A.BOARD_BODY , A.DELETE_YN , A.CREATED_DATE , A.MODIFIED_DATE , D.NAME FROM TBL_BOARD A LEFT JOIN TBL_MEMBER D ON(A.MEMBER_NO = D.MEMBER_NO)
D.NAME LIKE '%' || #{ searchValue } || '%'
A.BOARD_TITLE LIKE '%' || #{ searchValue } || '%'
A.BOARD_BODY LIKE '%' || #{ searchValue } || '%'
AND A.DELETE_YN = 'N' AND A.BOARD_TYPE = '공지'
ORDER BY A.BOARD_NO DESC ) B
WHERE ROWNUM <= #{ endRow }
) C WHERE C.RNUM >= #{ startRow }
