mapper.insert:
		INSERT
		  INTO TBL_BOARD_ATTACHMENT
		  	 (
		  	   BOARD_NO
		  	 , ATTACHMENT_NO
		  	 , ORIGINAL_NAME
		  	 , SAVED_NAME
		  	 , SAVE_PATH
		  	 , THUMBNAIL_PATH
		  	 , ATTACHMENT_STATUS
		  	 )
		  	 VALUES
		  	 (
          	   SEQ_BOARD_NO.NEXTVAL
		  	 , #{ attachmentNo }
		  	 , #{ originFileName }
		  	 , #{ savedFileName }
		  	 , #{ savePath }
		  	 , #{ thumbnailPath }
		  	 , DEFAULT
		  	 )
