mapper.select:
SELECT /* com.qs.www.main.model.dao.MainDAO#selectWorkingType() */
A.CHECK_IN_TIME , A.CHECK_OUT_TIME , A.BREAK_START_TIME , A.BREAK_END_TIME FROM TBL_STANDARD_WORK A WHERE A.WORK_CODE = #{ workNo }
B.DAY_OF_WEEK , B.CHECK_IN_TIME , B.CHECK_OUT_TIME , A.BREAK_START_TIME , A.BREAK_END_TIME FROM TBL_CUSTOM_WORK A JOIN TBL_CUSTOM_WORKTIME B ON (A.WORK_NO = B.WORK_NO) WHERE A.WORK_NO = #{ workNo } AND B.DAY_OF_WEEK = #{ selectedDayOfWeek }
