mapper.select:
SELECT /* com.qs.www.schedule.mng.working.dao.MngAppliedWorkingDAO#selectAllMemberWorkReport() */ C.RNUM , C.REPORT_NO , C.REPORT_DATE , C.MEMBER_NO , C.DOCUMENT_NO , C.REPORT_NOTE , C.REPORT_STATUS , C.LINE_NAME , C.REPORT_TITLE FROM (SELECT ROWNUM RNUM , B.REPORT_NO , B.REPORT_DATE , B.MEMBER_NO , B.DOCUMENT_NO , B.REPORT_NOTE , B.REPORT_STATUS , B.LINE_NAME , B.REPORT_TITLE FROM (SELECT A.REPORT_NO , A.REPORT_DATE , A.MEMBER_NO , A.DOCUMENT_NO , A.REPORT_NOTE , A.REPORT_STATUS , A.LINE_NAME , A.REPORT_TITLE FROM TBL_REPORT A
A.REPORT_TITLE LIKE '%' || #{ selectCriteria.searchValue } || '%'
A.REPORT_STATUS LIKE '%' || #{ selectCriteria.searchValue } || '%'
AND A.DOCUMENT_NO IN (#{ documentNo1 }, #{ documentNo2 })
ORDER BY A.REPORT_NO DESC ) B
WHERE ROWNUM <= #{ selectCriteria.endRow }
) C WHERE C.RNUM >= #{ selectCriteria.startRow }

