SELECT /*com.qs.www.mng.schedule.model.dao.MngCommuteDAO#selectAllMemberCount() */ C.RNUM , C.MEMBER_NO , C.DEPT_NAME , C.JOB_NAME FROM (SELECT ROWNUM RNUM , B.MEMBER_NO , B.DEPT_NAME , B.JOB_NAME FROM (SELECT A.MEMBER_NO , D.DEPT_NAME , E.JOB_NAME FROM TBL_MEMBER A LEFT JOIN TBL_DEPARTMENT D ON (A.DEPT_CODE = D.DEPT_CODE) LEFT JOIN TBL_JOB E ON (A.JOB_CODE = E.JOB_CODE)
A.NAME LIKE '%' || #{ selectCriteria.searchValue } || '%'
D.DEPT_NAME LIKE '%' || #{ selectCriteria.searchValue } || '%'
E.JOB_NAME '%' || #{ selectCriteria.searchValue } || '%'
AND A.ENT_YN = 'N' AND A.MEMBER_NO = #{ selectCriteria. }
ORDER BY A.MEMBER_NO DESC ) B
WHERE ROWNUM <= #{ selectCriteria.endRow }
) C WHERE C.RNUM >= #{ selectCriteria.startRow }
