mapper.select:
SELECT C.RNUM , C.REPORT_NO , C.REPORT_DATE , C.MEMBER_NO , C.DOCUMENT_NO , C.REPORT_NOTE , C.REPORT_STATUS , C.LINE_NAME , C.REPORT_TITLE FROM (SELECT ROWNUM RNUM , B.REPORT_NO , B.REPORT_DATE , B.MEMBER_NO , B.DOCUMENT_NO , B.REPORT_NOTE , B.REPORT_STATUS , B.LINE_NAME , B.REPORT_TITLE FROM (SELECT A.REPORT_NO , A.REPORT_DATE , A.MEMBER_NO , A.DOCUMENT_NO , A.REPORT_NOTE , A.REPORT_STATUS , A.LINE_NAME , A.REPORT_TITLE FROM TBL_REPORT A
A.REPORT_TITLE LIKE '%' || #{ selectCriteria.searchValue } || '%'
A.REPORT_STATUS LIKE '%' || #{ selectCriteria.searchValue } || '%'
AND A.DOCUMENT_NO IN (#{ documentNo1 }, #{ documentNo2 }) AND A.MEMBER_NO = #{ memberNo } AND A.REPORT_STATUS != '회수'
ORDER BY A.REPORT_NO DESC ) B
WHERE ROWNUM <= #{ selectCriteria.endRow }
) C WHERE C.RNUM >= #{ selectCriteria.startRow }

SELECT
       COUNT(*)
  FROM TBL_COMMUTE A
 WHERE A.MEMBER_NO = #{ memberNo }
   AND (A.YEAR_MONTH || '-' || A.DAY) = #{ selectedDate }
   AND A.OUT_TIME IS NULL

mapper.insert:
   INSERT /* com.qs.www.schedule.model.dao.ScheduleDAO#insertCustomWorktime() */
	  INTO TBL_CUSTOM_WORKTIME A
	(
	  A.WORK_NO
	, A.DAY_OF_WEEK
	, A.CHECK_IN_TIME
	, A.CHECK_OUT_TIME
	)
	VALUES
	(
	  #{ workNo }
	, #{ dayOfWeek }
	, #{ checkInTime }
	, #{ checkOutTime }
	)

INSERT /* com.qs.www.schedule.model.dao.ScheduleDAO#overtimeLogDTO() */
  INTO TBL_MEMBER_OVERTIME_LOG A
(
  A.MEMBER_OVERTIME_LOG_NO
, A.OVERTIME_REPORT_NO
, A.MEMBER_NO
, A.OVERTIME_START_DAY
, A.OVERTIME_START_TIME
, A.OVERTIME_END_DAY
, A.OVERTIME_END_TIME
, A.OVERTIME_DURING
, A.TRANS_BILL
)
VALUES
(
  SEQ_MEMBER_OVERTIME_LOG_NO.NEXTVAL
, #{ overtimeReportNo }
, #{ memberNo }
, #{ overtimeStartDay }
, #{ overtimeStartTime }
, #{ overtimeEndDay }
, #{ overtimeEndTime }
, #{ overtimeDuring }
, 'N'
)
