mapper.insert:
INSERT
  INTO TBL_ITEM_CONTENT A
(
  A.REPORT_NO
, A.DOCUMENT_NO
, A.PRIORITY
, A.ITEM_CONTENT
)
VALUES
(
  #{ reportNo }
, #{ documentNo }
, #{ priority }
, #{ itemContent }
)

INSERT
  INTO TBL_APPROVER_PER_REPORT
(
  A.REPORT_NO
, A.MEMBER_NO
, A.PRIROTY
, A.APPROVER_TYPE
)
VALUES
(
  #{ reportNo }
, #{ memberNo }
, #{ priority }
, DEFAULT
)
