mapper.insert:
INSERT /* com.qs.www.schedule.model.dao.ScheduleDAO#applyWorkingSystem() */
  INTO TBL_REPORT A
(
  A.REPORT_NO
, A.REPORT_DATE
, A.MEMBER_NO
, A.DOCUMENT_NO
, A.REPORT_NOTE
, A.REPORT_STATUS
, A.LINE_NAME
, A.REPORT_TITLE
)
VALUES
(
  SEQ_REPORT_NO.NEXTVAL
, SYSDATE
, #{ memberNo }
, #{ documentNo }
, #{ reportNote }
, DEFAULT
, #{ lineName }
, #{ reportTitle }
)

INSERT /* com.qs.www.schedule.model.dao.ScheduleDAO#applyWorkingSystemItemContent() */
  INTO TBL_ITEM_CONTENT A
(
  A.REPORT_NO
, A.DOCUMENT_NO
, A.PRIORITY
, A.ITEM_CONTENT
)
VALUES
(
  #{ reportNo }
, #{ documentNo }
, #{ priority }
, #{ itemContent }
)

INSERT /* com.qs.www.schedule.model.dao.ScheduleDAO#applyWorkingSystemApprover() */
