mapper.select:
SELECT /* com.qs.www.welfare.model.dao.WelfareDAO#selectEventNo() */
	   EVENT_NO
  FROM TBL_FAMILY_EVENT_TYPE
 WHERE EVENT_TYPE = #{ eventType }
   AND EVENT_RELATED_PERSON = #{ relation }
   AND WREATH_STATUS = #{ wreathStatus }

SELECT /* com.qs.www.approval.model.dao.ApprovalDAO#selectReportDetail()*/ C.* FROM (SELECT ROWNUM RNUM , B.* FROM (SELECT A.REPORT_NO , A.REPORT_DATE , A.MEMBER_NO , A.DOCUMENT_NO , A.REPORT_NOTE , A.REPORT_STATUS , A.LINE_NAME , A.REPORT_TITLE FROM TBL_REPORT A LEFT JOIN TBL_MEMBER D ON(A.MEMBER_NO = D.MEMBER_NO) LEFT JOIN TBL_DOCUMENT_FORM E ON(A.DOCUMENT_NO = E.DOCUMENT_NO)
E.DOCUMENT_NAME LIKE '%' || #{ selectCriteria.searchValue } || '%'
A.REPORT_STATUS LIKE '%' || #{ selectCriteria.searchValue } || '%'
AND A.MEMBER_NO = #{ memberNo } AND A.DOCUMENT_NO IN(7,8,9,10,11,12) AND REPORT_STATUS != '회수'
ORDER BY A.REPORT_NO DESC ) B
WHERE ROWNUM <= #{ selectCriteria.endRow }
) C WHERE C.RNUM >= #{ selectCriteria.startRow }

SELECT /* com.qs.www.approval.model.dao.ApprovalDAO#selectReportDetail()*/ C.* FROM (SELECT ROWNUM RNUM , B.* FROM (SELECT A.REPORT_NO , A.REPORT_DATE , A.MEMBER_NO , D.NAME , A.DOCUMENT_NO , A.REPORT_NOTE , A.REPORT_STATUS , A.LINE_NAME , A.REPORT_TITLE FROM TBL_REPORT A LEFT JOIN TBL_MEMBER D ON(A.MEMBER_NO = D.MEMBER_NO) LEFT JOIN TBL_DOCUMENT_FORM E ON(A.DOCUMENT_NO = E.DOCUMENT_NO)
E.DOCUMENT_NAME LIKE '%' || #{ searchValue } || '%'
D.NAME LIKE '%' || #{ searchValue } || '%'
A.REPORT_STATUS LIKE '%' || #{ searchValue } || '%'
AND A.DOCUMENT_NO IN(7,8,9,10,11,12)
ORDER BY A.REPORT_NO DESC ) B
WHERE ROWNUM <= #{ endRow }
) C WHERE C.RNUM >= #{ startRow }

SELECT /* com.qs.www.welfare.model.dao.WelfareDAO#selectSeminarRoomByMemberNo() */ C.* FROM (SELECT ROWNUM RNUM , B.* FROM (SELECT LOG.MEETING_ROOM_NO , LOG.USE_DATE , LOG.RESERV_TIME_NO , TIME.RESERV_TIME , LOG.SEMINAR_INFO FROM TBL_MEETING_ROOM_LOG LOG LEFT JOIN TBL_MEETING_ROOM_TIME TIME ON(LOG.RESERV_TIME_NO = TIME.RESERV_NO)
LOG.MEETING_ROOM_NO LIKE '%' || #{ selectCriteria.searchValue } || '%'
LOG.USE_DATE LIKE '%' || #{ selectCriteria.searchValue } || '%'
LOG.RESERV_TIME_NO LIKE '%' || #{ selectCriteria.searchValue } || '%'
AND LOG.MEMBER_NO = #{ memberNo }
ORDER BY LOG.MEETING_ROOM_NO DESC ) B
WHERE ROWNUM <= #{ selectCriteria.endRow }
) C WHERE C.RNUM >= #{ selectCriteria.startRow }

